package com.example.myapp.coreimport com.google.gson.GsonBuilderimport retrofit2.Retrofitimport retrofit2.converter.gson.GsonConverterFactoryobject Api {    private val URL = "http://192.168.0.76:3000/"    private var gson = GsonBuilder().create()    val retrofit = Retrofit.Builder()        .baseUrl(URL)        .addConverterFactory(GsonConverterFactory.create(gson))        .build()}